mapper.select:
SELECT id, routine_id, log_date, status, checked_at
FROM routine_log
WHERE routine_id = #{routineId}
  AND log_date = #{logDate}

SELECT rl.id, rl.routine_id, rl.log_date, rl.status, rl.checked_at
FROM routine_log rl
INNER JOIN routine r
  ON r.id = rl.routine_id
WHERE r.user_id = #{userId}
  AND rl.log_date >= #{startDate}
  AND rl.log_date <= #{endDate}
ORDER BY rl.log_date ASC, rl.checked_at ASC
